package poo;

public class Coche {

	// TODO Auto-generated method stub

	private int ruedas;
	private int largo;
	private int ancho;
	private int peso;
	private boolean aire;
	private boolean gps;

	// constructor//
	public Coche() {
		ruedas = 4;
		largo = 4;
		ancho = 4;

	}

	public int getRuedas() {
		return ruedas;
	}

	public void setRuedas(int ruedas) {
		this.ruedas = ruedas;
	}

	public int getLargo() {
		return largo;
	}

	public void setLargo(int largo) {
		this.largo = largo;
	}

	public int getAncho() {
		return ancho;
	}

	public void setAncho(int ancho) {
		this.ancho = ancho;
	}

	public int getPeso() {
		return peso;
	}

	public void setPeso(int peso) {
		this.peso = peso;
	}

	
	public boolean isAire() {
		return aire;
	}

	public void setAire(boolean aire) {
		this.aire = aire;		
	}

	
	public boolean isGps() {
		return gps;
	}

	public void setGps(boolean gps) {
		this.gps = gps;
	}
	 
	
	 
	

}
